import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Header.scss";

const Header = () => {
  const navigate = useNavigate();
  const [token, setToken] = useState<string | null>(null);

  // Додаємо слухач подій для відслідковування зміни в localStorage
  useEffect(() => {
    const handleStorageChange = () => {
      const storedToken = localStorage.getItem("token");
      setToken(storedToken); // Оновлюємо стан при зміні токену в localStorage
    };

    // Підписуємось на подію зміни localStorage
    window.addEventListener("storage", handleStorageChange);

    // Перевіряємо наявність токену при першому рендері
    const storedToken = localStorage.getItem("token");
    setToken(storedToken);

    // Очищаємо слухач подій при демонтажі компонента
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []); // Виконати тільки при першому рендері

  // Функція для виходу
  const handleLogout = () => {
    localStorage.removeItem("token"); // Видаляємо токен при виході
    setToken(null); // Оновлюємо стан
    navigate("/login"); // Перенаправляємо на сторінку логіну
  };

  return (
    <header className="header">
      <div className="logo">
        <Link to="/" className="logo-link">
          <h1>Готель "Хуйня"</h1>
        </Link>
      </div>
      <nav>
        <ul>
          <li><Link to="/rooms">Номери</Link></li>
          <li><Link to="/contacts">Контакти</Link></li>
          {token ? (
            <>
              <li><Link to="/dashboard">Особистий кабінет</Link></li>
              <li>
                <button onClick={handleLogout} className="logout-button">Вийти</button>
              </li>
            </>
          ) : (
            <li><Link to="/login">Увійти</Link></li>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
