import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios, { AxiosResponse, AxiosError } from "axios";
import { Room } from "../../types/Room";
import "./ManageRoomsPage.scss";

const ManageRoomsPage = () => {
  const [rooms, setRooms] = useState<Room[]>([]);
  const [formState, setFormState] = useState<Room | null>(null);
  const [selectedPhoto, setSelectedPhoto] = useState<File | null>(null); // –î–ª—è —Ñ–æ—Ç–æ
  const [error, setError] = useState<string>("");
  const [formError, setFormError] = useState<string | null>(null);

  const navigate = useNavigate();
  const { roomId } = useParams<{ roomId?: string }>();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const decodedToken = JSON.parse(atob(token.split(".")[1]));
    if (decodedToken.role !== "manager") {
      navigate("/rooms");
    }

    if (roomId) {
      axios
        .get(`http://localhost:5000/rooms/${roomId}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response: AxiosResponse<Room>) => {
          setFormState(response.data);
        })
        .catch((error: AxiosError) => {
          setError("–ù–µ –≤–¥–∞–ª–æ—Å—è –∑–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ –Ω–æ–º–µ—Ä –¥–ª—è —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è");
          console.error(error);
        });
    } else {
      setFormState({
        id: 0,
        name: "",
        description: "",
        capacity: 1,
        price: 0,
        available_from: "",
        available_to: "",
        photo_url: "", // –î–æ–¥–∞–Ω–æ —Ñ–æ—Ç–æ_url –¥–ª—è –Ω–æ–≤–∏—Ö –Ω–æ–º–µ—Ä—ñ–≤
      });
    }

    axios
      .get("http://localhost:5000/rooms", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response: AxiosResponse<Room[]>) => {
        setRooms(response.data);
      })
      .catch((error: AxiosError) => {
        setError("–ù–µ –≤–¥–∞–ª–æ—Å—è –∑–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ –Ω–æ–º–µ—Ä–∏");
        console.error(error);
      });
  }, [navigate, roomId]);

const handleFormSubmit = async (e: React.FormEvent) => {
  e.preventDefault();
  const token = localStorage.getItem("token");

  if (!formState || !token) return;

  const {
    id,
    name,
    description,
    capacity,
    price,
    available_from,
    available_to,
  } = formState;

  // üîé –í–∞–ª—ñ–¥–∞—Ü—ñ—è: –≤—Å—ñ –ø–æ–ª—è –º–∞—é—Ç—å –±—É—Ç–∏ –∑–∞–ø–æ–≤–Ω–µ–Ω—ñ
  if (
    !name.trim() ||
    !description.trim() ||
    !capacity ||
    !price ||
    !available_from ||
    !available_to ||
    (!id && !selectedPhoto) // –§–æ—Ç–æ –æ–±–æ–≤‚Äô—è–∑–∫–æ–≤–µ —Ç—ñ–ª—å–∫–∏ –ø—Ä–∏ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—ñ
  ) {
    setError("–ë—É–¥—å –ª–∞—Å–∫–∞, –∑–∞–ø–æ–≤–Ω—ñ—Ç—å —É—Å—ñ –ø–æ–ª—è.");
    return;
  }

  const formData = new FormData();
  if (selectedPhoto) {
    formData.append("photo", selectedPhoto);
  }

  try {
    let response: AxiosResponse<Room>;

    if (id && id !== 0) {
      // –†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
      response = await axios.put(
        `http://localhost:5000/rooms/${id}`,
        { name, description, capacity, price, available_from, available_to },
        { headers: { Authorization: `Bearer ${token}` } }
      );
    } else {
      // –°—Ç–≤–æ—Ä–µ–Ω–Ω—è
      response = await axios.post(
        "http://localhost:5000/rooms",
        { name, description, capacity, price, available_from, available_to },
        { headers: { Authorization: `Bearer ${token}` } }
      );
    }

    if (selectedPhoto && response.data.id) {
      await axios.post(
        `http://localhost:5000/upload-photo/${response.data.id}`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setRooms((prevRooms) =>
        prevRooms.map((room) =>
          room.id === response.data.id
            ? { ...room, photo_url: `/uploads/rooms/${response.data.id}.png` }
            : room
        )
      );
    }

    setFormState(null);
    setRooms([...rooms, response.data]);
    window.location.reload();
  } catch (error) {
    setError("–ù–µ –≤–¥–∞–ª–æ—Å—è –∑–±–µ—Ä–µ–≥—Ç–∏ –∑–º—ñ–Ω–∏");
    console.error(error);
  }
};

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    if (name === "capacity" || name === "price") {
      if (!/^\d*$/.test(value)) return;
    }

    if (formState) {
      setFormState({ ...formState, [name]: value });
    }
  };

  const handlePhotoChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedPhoto(file); // –û–Ω–æ–≤–ª—é—î–º–æ –≤–∏–±—Ä–∞–Ω–µ —Ñ–æ—Ç–æ
    }
  };

  const handleDelete = async (id: number) => {
    const token = localStorage.getItem("token");

    if (!token) return;

    try {
      await axios.delete(`http://localhost:5000/rooms/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setRooms(rooms.filter((room) => room.id !== id));
    } catch (error) {
      setError("–ù–µ –≤–¥–∞–ª–æ—Å—è –≤–∏–¥–∞–ª–∏—Ç–∏ –Ω–æ–º–µ—Ä");
      console.error(error);
    }
  };

  const formatDate = (date: string) => {
    const formattedDate = new Date(date);
    return formattedDate.toLocaleDateString("uk-UA");
  };

  return (
    <div className="manage-rooms-page">
      {error && <div className="error">{error}</div>}

      <div className="form-section">
        <h2>
          {formState?.id && formState.id !== 0
            ? "–†–µ–¥–∞–≥—É–≤–∞—Ç–∏ –Ω–æ–º–µ—Ä"
            : "–î–æ–¥–∞—Ç–∏ –Ω–æ–≤–∏–π –Ω–æ–º–µ—Ä"}
        </h2>
{error && <div className="form-error">{error}</div>}

        <form onSubmit={handleFormSubmit}>
          <div className="form-group">
            <label htmlFor="name">–ù–∞–∑–≤–∞ –Ω–æ–º–µ—Ä–∞:</label>
            <input
              type="text"
              name="name"
              value={formState?.name || ""}
              onChange={handleInputChange}
              id="name"
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">–û–ø–∏—Å –Ω–æ–º–µ—Ä–∞:</label>
            <input
              type="text"
              name="description"
              value={formState?.description || ""}
              onChange={handleInputChange}
              id="description"
            />
          </div>

          <div className="form-group">
            <label htmlFor="capacity">–ö—ñ–ª—å–∫—ñ—Å—Ç—å –æ—Å—ñ–±:</label>
            <input
              type="text"
              name="capacity"
              value={formState?.capacity || ""}
              onChange={handleInputChange}
              id="capacity"
            />
          </div>

          <div className="form-group">
            <label htmlFor="price">–¶—ñ–Ω–∞:</label>
            <input
              type="text"
              name="price"
              value={formState?.price || ""}
              onChange={handleInputChange}
              id="price"
            />
          </div>

          <div className="form-group">
            <label htmlFor="available_from">–î–æ—Å—Ç—É–ø–Ω–∏–π –∑:</label>
            <input
              type="date"
              name="available_from"
              value={formState?.available_from || ""}
              onChange={handleInputChange}
              id="available_from"
            />
          </div>

          <div className="form-group">
            <label htmlFor="available_to">–î–æ—Å—Ç—É–ø–Ω–∏–π –¥–æ:</label>
            <input
              type="date"
              name="available_to"
              value={formState?.available_to || ""}
              onChange={handleInputChange}
              id="available_to"
            />
          </div>

          <div className="form-group">
            <label htmlFor="photo">–ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ —Ñ–æ—Ç–æ:</label>
            <input type="file" id="photo" onChange={handlePhotoChange} />
          </div>

          <button type="submit">
            {formState?.id ? "–ó–±–µ—Ä–µ–≥—Ç–∏ –∑–º—ñ–Ω–∏" : "–î–æ–¥–∞—Ç–∏ –Ω–æ–º–µ—Ä"}
          </button>
        </form>
      </div>

      <div className="room-list-section">
        <h3>–î–æ—Å—Ç—É–ø–Ω—ñ –Ω–æ–º–µ—Ä–∏:</h3>
<ul>
  {rooms.map((room) => (
    <li key={room.id}>
      {room.photo_url && (
        <img
          src={`http://localhost:5000${room.photo_url}`}
          alt="Room"
        />
      )}

      <div className="details">
        <div className="info">
          <p><strong>–ù–∞–∑–≤–∞:</strong> {room.name}</p>
          <p><strong>–¶—ñ–Ω–∞:</strong> {room.price} –≥—Ä–Ω</p>
          <p><strong>–ö—ñ–ª—å–∫—ñ—Å—Ç—å –æ—Å—ñ–±:</strong> {room.capacity}</p>
          <p><strong>–û–ø–∏—Å:</strong> {room.description}</p>
          <p><strong>–î–æ—Å—Ç—É–ø–Ω–∏–π –∑:</strong> {formatDate(room.available_from)}</p>
          <p><strong>–î–æ—Å—Ç—É–ø–Ω–∏–π –¥–æ:</strong> {formatDate(room.available_to)}</p>
        </div>

        <div className="actions">
          <button onClick={() => setFormState(room)}>–†–µ–¥–∞–≥—É–≤–∞—Ç–∏</button>
          <button onClick={() => handleDelete(room.id)}>–í–∏–¥–∞–ª–∏—Ç–∏</button>
        </div>
      </div>
    </li>
  ))}
</ul>

      </div>
    </div>
  );
};

export default ManageRoomsPage;
