import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Room } from "../../types/Room"; // Імпортуємо тип Room

const ManageRoomsPage = () => {
  const [rooms, setRooms] = useState<Room[]>([]); // Стан для збереження номерів
  const [error, setError] = useState(""); // Стан для помилок
  const [editRoom, setEditRoom] = useState<Room | null>(null); // Для редагування номера
  const [isNewRoom, setIsNewRoom] = useState(false); // Перевірка для нового номера
  const navigate = useNavigate();

  // Завантажуємо номери при завантаженні компонента
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const decodedToken = JSON.parse(atob(token.split(".")[1])); // Декодуємо токен
    if (decodedToken.role !== "manager") {
      navigate("/rooms"); // Якщо не менеджер, перенаправляємо на сторінку з номерами
    }

    // Отримання всіх номерів
    fetch("http://localhost:5000/rooms", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then((data) => setRooms(data)) // Зберігаємо отримані номери в стан
      .catch((err) => setError("Помилка при отриманні даних"));
  }, [navigate]);

  // Обробник для видалення номера
  const handleDelete = (roomId: number) => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/rooms/${roomId}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then(() => {
        // Оновлюємо список номерів після видалення
        setRooms(rooms.filter((room) => room.id !== roomId));
      })
      .catch((err) => setError("Помилка при видаленні номера"));
  };

  // Обробник для редагування номера
  const handleEdit = (room: Room) => {
    setEditRoom(room); // Встановлюємо дані номера для редагування
    setIsNewRoom(false); // Вказуємо, що це не новий номер
  };

  // Обробник для додавання нового номера
  const handleAddNewRoom = () => {
    setEditRoom(null); // Очищаємо форму для нового номера
    setIsNewRoom(true); // Вказуємо, що ми додаємо новий номер
  };

  // Обробник для відправлення форми
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const roomData = editRoom || {
      name: "",
      description: "",
      capacity: 0,
      price: 0,
      available_from: "",
      available_to: "",
    };

    const url = isNewRoom
      ? "http://localhost:5000/rooms" // Для додавання нового номера
      : `http://localhost:5000/rooms/${editRoom?.id}`; // Для редагування існуючого номера

    const method = isNewRoom ? "POST" : "PUT"; // Вибір методу

    const body = JSON.stringify({
      name: roomData.name,
      description: roomData.description,
      capacity: roomData.capacity,
      price: roomData.price,
      available_from: roomData.available_from,
      available_to: roomData.available_to,
    });

    fetch(url, {
      method,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body,
    })
      .then((response) => response.json())
      .then(() => {
        setEditRoom(null); // Очищаємо форму після успішного редагування
        setIsNewRoom(false); // Повертаємося до стандартного режиму
        window.location.reload(); // Перезавантажуємо сторінку, щоб побачити оновлені дані
      })
      .catch((err) => setError("Помилка при збереженні номера"));
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>, field: string) => {
    if (editRoom) {
      setEditRoom({
        ...editRoom,
        [field]: e.target.value,
      });
    }
  };

  return (
    <div>
      <h2>Управління номерами</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Кнопка для додавання нового номера */}
      <button onClick={handleAddNewRoom}>Додати новий номер</button>

      {/* Форма для додавання/редагування номера */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Назва номера"
          value={editRoom?.name || ""}
          onChange={(e) => handleChange(e, "name")}
        />
        <input
          type="text"
          placeholder="Опис"
          value={editRoom?.description || ""}
          onChange={(e) => handleChange(e, "description")}
        />
        <input
          type="number"
          placeholder="Кількість місць"
          value={editRoom?.capacity || ""}
          onChange={(e) => handleChange(e, "capacity")}
        />
        <input
          type="number"
          placeholder="Ціна"
          value={editRoom?.price || ""}
          onChange={(e) => handleChange(e, "price")}
        />
        <input
          type="date"
          placeholder="Дата доступності"
          value={editRoom?.available_from || ""}
          onChange={(e) => handleChange(e, "available_from")}
        />
        <input
          type="date"
          placeholder="Дата закінчення"
          value={editRoom?.available_to || ""}
          onChange={(e) => handleChange(e, "available_to")}
        />
        <button type="submit">
          {isNewRoom ? "Додати номер" : "Редагувати номер"}
        </button>
      </form>

      {/* Список номерів */}
      <ul>
        {rooms.length > 0 ? (
          rooms.map((room) => (
            <li key={room.id}>
              <p>
                {room.name} - {room.price} грн/день
              </p>
              <button onClick={() => handleEdit(room)}>Редагувати</button>
              <button onClick={() => handleDelete(room.id)}>Видалити</button>
            </li>
          ))
        ) : (
          <p>Немає доступних номерів для редагування</p>
        )}
      </ul>
    </div>
  );
};

export default ManageRoomsPage;
