import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Room } from "../../types/Room"; // Тип Room
import "./RoomDetailsPage.scss";

const RoomDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState<Room | null>(null); // Використовуємо тип Room
  const [checkInDate, setCheckInDate] = useState<string | null>(null);
  const [checkOutDate, setCheckOutDate] = useState<string | null>(null);
  const [available, setAvailable] = useState<boolean | null>(null);
  const [error, setError] = useState<string | null>(null);

  // Завантаження номеру по id
  useEffect(() => {
    fetch(`http://localhost:5000/rooms/${id}`)
      .then((response) => response.json())
      .then((data) => setRoom(data))
      .catch((err) => {
        setError("Помилка при завантаженні номеру");
        console.error(err);
      });
  }, [id]);

  // Перевірка доступності номера на вказані дати
  const checkAvailability = async () => {
    if (!checkInDate || !checkOutDate) {
      setError("Будь ласка, виберіть дати.");
      return;
    }

    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/check-availability", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          roomId: id,
          checkIn: checkInDate,
          checkOut: checkOutDate,
        }),
      });

      const data = await response.json();
      setAvailable(data.available);
    } catch (err) {
      setError("Помилка при перевірці доступності");
      console.error(err);
    }
  };

  const handleBooking = () => {
    if (available) {
      navigate(`/book/${id}`, { state: { checkInDate, checkOutDate } });
    } else {
      setError("Цей номер не доступний на обрані дати.");
    }
  };

  // Функція для повернення на сторінку Номери
  const goBackToRooms = () => {
    navigate("/rooms");
  };

  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!room) return <p>Завантаження номеру...</p>;

return (
  <div className="dashboard-page">
    <div className="dashboard-wrapper">
      {error && <p className="error-text">{error}</p>}

      {user ? (
        <h1 className="greeting">
          Вітаємо, {user.first_name} {user.last_name}
        </h1>
      ) : (
        <p>Завантаження...</p>
      )}

      <h2 className="section-title">Ваші бронювання</h2>

      {bookings.length > 0 ? (
        <div className="booking-list">
          {bookings.map((booking) => (
            <div key={booking.id} className="booking-container">
              {booking.photo_url && (
                <div className="image-wrapper">
                  <img
                    src={`http://localhost:5000${booking.photo_url}`}
                    alt={booking.name}
                  />
                </div>
              )}

              <div className="info">
                <h3>{booking.name}</h3>
                <p className="description">{booking.description}</p>
                <p className="info-line">Місткість: {booking.capacity} осіб</p>
                <p className="info-line">Дата заїзду: {formatDate(booking.check_in)}</p>
                <p className="info-line">Дата виїзду: {formatDate(booking.check_out)}</p>
                <p className="info-line">Сума: {booking.total_amount || "Немає суми"} грн</p>
              </div>

              <div className="actions">
                <button onClick={() => handleCancelBooking(booking.id)}>
                  Скасувати бронювання
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="no-bookings">У вас немає бронювань</p>
      )}
    </div>
  </div>
);

};

export default RoomDetailsPage;
