// src/pages/Dashboard.tsx
import React, { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";  // Імпортуємо AuthContext

const Dashboard = () => {
  const [bookings, setBookings] = useState<any[]>([]);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();
  const authContext = useContext(AuthContext);

  if (!authContext) {
    throw new Error('AuthContext is not available');
  }

  const { token, logout } = authContext;

  // Якщо користувач не авторизований, перенаправляємо на сторінку логіну
  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else {
      fetch("http://localhost:5000/user/bookings", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => response.json())
        .then((data) => setBookings(data))
        .catch((err) => {
          setError("Помилка при отриманні бронювань");
          console.error(err);
        });
    }
  }, [navigate, token]);

  const handleLogout = () => {
    logout();  // Викликаємо метод logout з контексту
    navigate('/login');  // Перенаправляємо на сторінку логіну
  };

  const handleCancelBooking = async (bookingId: string) => {
    if (!token) return;

    try {
      const response = await fetch(
        `http://localhost:5000/user/bookings/${bookingId}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.ok) {
        setBookings(bookings.filter((booking) => booking.id !== bookingId));
      } else {
        setError("Не вдалося скасувати бронювання");
      }
    } catch (err) {
      setError("Помилка при скасуванні бронювання");
      console.error(err);
    }
  };

  return (
    <div>
      <h1>Особистий кабінет</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <h2>Ваші бронювання</h2>
      <ul>
        {bookings.map((booking) => (
          <li key={booking.id}>
            <p>Номер: {booking.roomName}</p>
            <p>Дата заїзду: {booking.check_in}</p>
            <p>Дата виїзду: {booking.check_out}</p>
            <button onClick={() => handleCancelBooking(booking.id)}>
              Скасувати бронювання
            </button>
          </li>
        ))}
      </ul>
      <button onClick={handleLogout}>Вийти</button>  {/* Кнопка "Вийти" на сторінці кабінету */}
    </div>
  );
};

export default Dashboard;
