// src/pages/RoomsPage.tsx
import React, { useEffect, useState } from "react";
import { useLocation, Link, useNavigate } from "react-router-dom";

const RoomsPage = () => {
  const [rooms, setRooms] = useState<any[]>([]);
  const [filteredRooms, setFilteredRooms] = useState<any[]>([]); // Збережемо відфільтровані номери
  const [error, setError] = useState<string | null>(null);
  const [sortOrder, setSortOrder] = useState<"asc" | "desc" | "none">("none");

  const location = useLocation();
  const navigate = useNavigate();
  const { checkInDate, checkOutDate } = location.state || {};

  // Завантажуємо номери з бази даних
  useEffect(() => {
    fetch("http://localhost:5000/rooms")
      .then((response) => response.json())
      .then((data) => {
        setRooms(data);
        filterAvailableRooms(data); // Фільтруємо номери після завантаження
      })
      .catch((err) => {
        setError("Помилка при завантаженні номерів");
        console.error(err);
      });
  }, [checkInDate, checkOutDate]);

  // Фільтруємо номери за доступністю на обрані дати
  const filterAvailableRooms = (rooms: any[]) => {
    if (checkInDate && checkOutDate) {
      const availableRooms = rooms.filter((room) => {
        // Логіка для перевірки доступності номеру на ці дати
        const booked = room.bookings.some((booking: any) => {
          return (
            (checkInDate >= booking.check_in && checkInDate <= booking.check_out) ||
            (checkOutDate >= booking.check_in && checkOutDate <= booking.check_out)
          );
        });
        return !booked; // Якщо номер не заброньовано на ці дати, додаємо його до доступних
      });
      setFilteredRooms(availableRooms); // Оновлюємо доступні номери
    }
  };

  // Сортуємо номери за ціною
  const sortRooms = (order: "asc" | "desc") => {
    const sorted = [...filteredRooms].sort((a, b) => {
      if (order === "asc") {
        return a.price - b.price;
      } else {
        return b.price - a.price;
      }
    });
    setFilteredRooms(sorted); // Оновлюємо відсортовані номери
  };

  const handleSortChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedOrder = e.target.value as "asc" | "desc" | "none";
    setSortOrder(selectedOrder);
    sortRooms(selectedOrder);
  };

  // Повертаємось на HomePage, щоб обрати інші дати
  const goBackToHomePage = () => {
    navigate("/"); // Перенаправлення на HomePage
  };

  return (
    <div>
      <h2>Номери</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <div>
        <label htmlFor="sortOrder">Сортувати за ціною: </label>
        <select
          id="sortOrder"
          value={sortOrder}
          onChange={handleSortChange}
        >
          <option value="none" disabled={sortOrder !== "none"}>Обрати</option>
          <option value="asc">За зростанням</option>
          <option value="desc">За спаданням</option>
        </select>
      </div>

      <ul>
        {filteredRooms.length > 0 ? (
          filteredRooms.map((room) => (
            <li key={room.id}>
              <p>{room.name} - {room.price} грн/добу</p>
              <Link to={`/rooms/${room.id}`}>
                <button>Переглянути номер</button>
              </Link>
            </li>
          ))
        ) : (
          <p>Немає доступних номерів на вибрані дати</p>
        )}
      </ul>

      {/* Кнопка для повернення на HomePage */}
      <button onClick={goBackToHomePage}>Обрати інші дати</button>
    </div>
  );
};

export default RoomsPage;
