import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Room } from "../../types/Room"; // Імпортуємо тип Room

const ManageRoomsPage = () => {
  const [rooms, setRooms] = useState<Room[]>([]);
  const [formState, setFormState] = useState<Room | null>(null); // Для форми додавання/редагування
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { roomId } = useParams(); // Отримуємо параметр roomId з URL

  // Завантаження номерв з бази
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const decodedToken = JSON.parse(atob(token.split(".")[1])); // Декодуємо токен
    if (decodedToken.role !== "manager") {
      navigate("/rooms"); // Якщо не менеджер, перенаправляємо на сторінку з номерами
    }

    fetch("http://localhost:5000/rooms", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then((data) => setRooms(data))
      .catch((err) => setError("Помилка при отриманні даних"));
  }, [navigate]);

  // Видалення номера
  const handleDelete = (roomId: number) => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/rooms/${roomId}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then(() => setRooms(rooms.filter((room) => room.id !== roomId)))
      .catch((err) => setError("Помилка при видаленні номера"));
  };

  // Форма для додавання або редагування номера
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const url = formState?.id
      ? `http://localhost:5000/rooms/${formState.id}`
      : "http://localhost:5000/rooms";
    const method = formState?.id ? "PUT" : "POST";

    fetch(url, {
      method,
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formState),
    })
      .then((response) => response.json())
      .then((data) => {
        setRooms((prevRooms) => {
          if (formState?.id) {
            return prevRooms.map((room) =>
              room.id === formState.id ? data : room
            );
          }
          return [data, ...prevRooms];
        });
        resetForm(); // Очистити форму після збереження
      })
      .catch((err) => setError("Помилка при збереженні номера"));
  };

  // Заповнення форми при редагуванні
  useEffect(() => {
    if (roomId) {
      const roomToEdit = rooms.find((room) => room.id === Number(roomId));
      if (roomToEdit) {
        setFormState(roomToEdit);
      }
    }
  }, [roomId, rooms]);

  // Скидання значень форми після додавання/редагування
const resetForm = () => {
  setFormState({
    id: 0, // Замість null використовуйте 0
    name: "",
    description: "",
    capacity: 1,
    price: 0,
    available_from: "",
    available_to: "",
  });
};


  return (
    <div className="manage-rooms-page">
      <h2>{formState ? "Редагування номера" : "Управління номерами"}</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Форма додавання або редагування */}
      <form onSubmit={handleSubmit} className="form-container">
        <div className="form-group">
          <label>Назва номера</label>
          <input
            type="text"
            value={formState?.name || ""}
            onChange={(e) =>
              setFormState({ ...formState!, name: e.target.value })
            }
            required
          />
        </div>

        <div className="form-group">
          <label>Опис</label>
          <textarea
            value={formState?.description || ""}
            onChange={(e) =>
              setFormState({ ...formState!, description: e.target.value })
            }
            required
          />
        </div>

        <div className="form-group">
          <label>Місткість</label>
          <input
            type="number"
            min="1" // Мінімальна кількість гостей
            value={formState?.capacity || 1}
            onChange={(e) =>
              setFormState({ ...formState!, capacity: Number(e.target.value) })
            }
            required
          />
        </div>

        <div className="form-group">
          <label>Ціна</label>
          <input
            type="number"
            min="0" // Мінімальна ціна
            value={formState?.price || 0}
            onChange={(e) =>
              setFormState({ ...formState!, price: Number(e.target.value) })
            }
            required
          />
        </div>

        <div className="form-group">
          <label>Дата доступності з</label>
          <input
            type="date"
            value={formState?.available_from || ""}
            onChange={(e) =>
              setFormState({ ...formState!, available_from: e.target.value })
            }
            required
          />
        </div>

        <div className="form-group">
          <label>Дата доступності до</label>
          <input
            type="date"
            value={formState?.available_to || ""}
            onChange={(e) =>
              setFormState({ ...formState!, available_to: e.target.value })
            }
            required
          />
        </div>

        <div className="form-group">
          <button type="submit">
            {formState?.id ? "Оновити номер" : "Додати номер"}
          </button>
        </div>
      </form>

      {/* Список номерів для менеджера */}
      <ul>
        {rooms.map((room) => (
          <li key={room.id}>
            <p>
              {room.name} - {room.price} грн/день
            </p>
            <button onClick={() => handleDelete(room.id)}>Видалити</button>
            <button onClick={() => navigate(`/manage-rooms/${room.id}`)}>
              Редагувати
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageRoomsPage;
