import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const RoomDetailsPage = () => {
  const { id } = useParams();
  const [room, setRoom] = useState<any>(null);
  const [checkIn, setCheckIn] = useState<string | null>(null);
  const [checkOut, setCheckOut] = useState<string | null>(null);
  const [isAvailable, setIsAvailable] = useState<boolean | null>(null);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  // Завантажуємо дані про номер
  useEffect(() => {
    fetch(`http://localhost:5000/rooms/${id}`)
      .then((response) => response.json())
      .then((data) => setRoom(data))
      .catch((err) => {
        setError("Помилка при завантаженні номеру");
        console.error(err);
      });
  }, [id]);

  const handleCheckAvailability = () => {
    if (checkIn && checkOut) {
      fetch("http://localhost:5000/check-availability", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ roomId: id, checkIn, checkOut }),
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.available) {
            setIsAvailable(true);
          } else {
            setIsAvailable(false);
          }
        })
        .catch((err) => {
          setError("Помилка при перевірці доступності");
          console.error(err);
        });
    }
  };

  const handleBook = () => {
    navigate(`/payment/${id}`, { state: { checkIn, checkOut } });
  };

  return (
    <div>
      {room ? (
        <div>
          <h2>{room.name}</h2>
          <p>{room.description}</p>
          <p>Ціна: {room.price} грн/день</p>
          <p>Місткість: {room.capacity} осіб</p>
          <label>Дата заїзду</label>
          <input type="date" onChange={(e) => setCheckIn(e.target.value)} />
          <label>Дата виїзду</label>
          <input type="date" onChange={(e) => setCheckOut(e.target.value)} />
          <button onClick={handleCheckAvailability}>Перевірити доступність</button>
          {isAvailable === null ? null : isAvailable ? (
            <button onClick={handleBook}>Забронювати</button>
          ) : (
            <p>Цей номер не доступний на обрані дати</p>
          )}
        </div>
      ) : (
        <p>Завантаження...</p>
      )}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default RoomDetailsPage;
