import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../../context/AuthContext"; // Імпортуємо AuthContext
import './AuthPage.scss'; // Імпортуємо стилі

const AuthPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState(""); // Поле для підтвердження пароля
  const [firstName, setFirstName] = useState(""); // Ім'я
  const [lastName, setLastName] = useState(""); // Прізвище
  const [isLogin, setIsLogin] = useState(true);

  // Стан для помилок кожного поля
  const [errors, setErrors] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    firstName: "",
    lastName: ""
  });

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const authContext = useContext(AuthContext);

  if (!authContext) {
    throw new Error("AuthContext is not available");
  }

  const { login } = authContext;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Очищаємо попередні помилки
    setErrors({
      email: "",
      password: "",
      confirmPassword: "",
      firstName: "",
      lastName: ""
    });

    // Валідація на клієнтській стороні
    let valid = true;

    // Перевірка на заповненість полів
    if (!email) {
      setErrors((prev) => ({ ...prev, email: "Будь ласка, введіть електронну пошту" }));
      valid = false;
    }

    if (!password) {
      setErrors((prev) => ({ ...prev, password: "Будь ласка, введіть пароль" }));
      valid = false;
    }

    if (!isLogin) {
      if (!firstName) {
        setErrors((prev) => ({ ...prev, firstName: "Будь ласка, введіть ім'я" }));
        valid = false;
      }
      if (!lastName) {
        setErrors((prev) => ({ ...prev, lastName: "Будь ласка, введіть прізвище" }));
        valid = false;
      }

      if (password !== confirmPassword) {
        setErrors((prev) => ({ ...prev, confirmPassword: "Паролі не збігаються" }));
        valid = false;
      }
    }

    if (!valid) return;

    setLoading(true);

    const endpoint = isLogin ? "login" : "register";
    const body = { email, password, firstName, lastName };

    try {
      const response = await fetch(`http://localhost:5000/${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrors((prev) => ({ ...prev, email: errorData.message || "Невірний логін або пароль" }));
        return;
      }

      const data = await response.json();
      login(data.token);

      if (data.token) {
        const decodedToken = JSON.parse(atob(data.token.split(".")[1]));

        if (decodedToken.role === "manager") {
          navigate("/manage-rooms");
        } else {
          navigate("/rooms");
        }
      } else {
        setErrors((prev) => ({ ...prev, email: "Токен не отримано" }));
      }
    } catch (err) {
      console.error(err);
      setErrors((prev) => ({ ...prev, email: "Помилка при підключенні до сервера" }));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-form-container">
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            placeholder="Введіть свою електронну адресу"
            type="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            id="email"
          />
          {errors.email && <p className="error-message">{errors.email}</p>} {/* Виведення помилки */}
        </div>

        <div className="form-group">
          <label htmlFor="password">Пароль</label>
          <input
            placeholder="Введіть свій пароль"
            type="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            id="password"
          />
          {errors.password && <p className="error-message">{errors.password}</p>} {/* Виведення помилки */}
        </div>

        {/* Покажемо поле для підтвердження пароля тільки на реєстрації */}
        {!isLogin && (
          <div className="form-group">
            <label htmlFor="confirmPassword">Підтвердьте пароль</label>
            <input
              placeholder="Підтвердьте свій пароль"
              type="password"
              name="confirmPassword"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              id="confirmPassword"
            />
            {errors.confirmPassword && <p className="error-message">{errors.confirmPassword}</p>} {/* Виведення помилки */}
          </div>
        )}

        {!isLogin && (
          <>
            <div className="form-group">
              <label htmlFor="firstName">Ім'я</label>
              <input
                placeholder="Вкажіть своє ім'я"
                type="text"
                name="firstName"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                id="firstName"
              />
              {errors.firstName && <p className="error-message">{errors.firstName}</p>} {/* Виведення помилки */}
            </div>

            <div className="form-group">
              <label htmlFor="lastName">Прізвище</label>
              <input
                placeholder="Вкажіть своє прізвище"
                type="text"
                name="lastName"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                id="lastName"
              />
              {errors.lastName && <p className="error-message">{errors.lastName}</p>} {/* Виведення помилки */}
            </div>
          </>
        )}

        <button type="submit" disabled={loading}>
          {loading ? "Зачекайте..." : isLogin ? "Увійти" : "Зареєструватися"}
        </button>
      </form>

      <p>
        {isLogin ? (
          <>
            Ще не зареєстровані?{" "}
            <button onClick={() => setIsLogin(false)}>Реєстрація</button>
          </>
        ) : (
          <>
            Вже маєте акаунт?{" "}
            <button onClick={() => setIsLogin(true)}>Увійти</button>
          </>
        )}
      </p>

      {errors.email && <p style={{ color: "red" }}>{errors.email}</p>}
    </div>
  );
};

export default AuthPage;
