import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Room } from "../../types/Room"; // Імпортуємо тип Room

const ManageRoomsPage = () => {
  const [rooms, setRooms] = useState<Room[]>([]);
  const [error, setError] = useState("");
  const [editRoom, setEditRoom] = useState<Room | null>(null); // Для редагування
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const decodedToken = JSON.parse(atob(token.split(".")[1])); // Декодуємо токен
    if (decodedToken.role !== "manager") {
      navigate("/rooms"); // Якщо не менеджер, перенаправляємо на сторінку з номерами
    }

    fetch("http://localhost:5000/rooms", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then((data) => setRooms(data))
      .catch((err) => setError("Помилка при отриманні даних"));
  }, [navigate]);

  // Обробник для видалення номера
  const handleDelete = (roomId: number) => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/rooms/${roomId}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then(() => setRooms(rooms.filter((room) => room.id !== roomId))) 
      .catch((err) => setError("Помилка при видаленні номера"));
  };

  // Обробник для редагування номера
  const handleEdit = (room: Room) => {
    setEditRoom(room); // Встановлюємо дані номера для редагування
  };

  // Обробник для відправлення форми
  const handleSubmit = async (e: React.FormEvent, room?: Room) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const url = room ? `http://localhost:5000/rooms/${room.id}` : "http://localhost:5000/rooms";
    const method = room ? "PUT" : "POST";
    const body = JSON.stringify({
      name: room?.name || "",
      description: room?.description || "",
      capacity: room?.capacity || 0,
      price: room?.price || 0,
      available_from: room?.available_from || "",
      available_to: room?.available_to || "",
    });

    fetch(url, {
      method,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body,
    })
      .then((response) => response.json())
      .then(() => {
        setEditRoom(null); // Очищаємо форму після успішного редагування
        window.location.reload(); // Перезавантажуємо сторінку, щоб побачити оновлені дані
      })
      .catch((err) => setError("Помилка при збереженні номера"));
  };

  return (
    <div>
      <h2>Управління номерами</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Форма для додавання/редагування номера */}
      <form onSubmit={(e) => handleSubmit(e, editRoom)}>
        <input
          type="text"
          placeholder="Назва номера"
          value={editRoom?.name || ""}
          onChange={(e) => setEditRoom({ ...editRoom, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Опис"
          value={editRoom?.description || ""}
          onChange={(e) => setEditRoom({ ...editRoom, description: e.target.value })}
        />
        <input
          type="number"
          placeholder="Кількість місць"
          value={editRoom?.capacity || ""}
          onChange={(e) => setEditRoom({ ...editRoom, capacity: +e.target.value })}
        />
        <input
          type="number"
          placeholder="Ціна"
          value={editRoom?.price || ""}
          onChange={(e) => setEditRoom({ ...editRoom, price: +e.target.value })}
        />
        <input
          type="date"
          placeholder="Дата доступності"
          value={editRoom?.available_from || ""}
          onChange={(e) => setEditRoom({ ...editRoom, available_from: e.target.value })}
        />
        <input
          type="date"
          placeholder="Дата закінчення"
          value={editRoom?.available_to || ""}
          onChange={(e) => setEditRoom({ ...editRoom, available_to: e.target.value })}
        />
        <button type="submit">{editRoom ? "Редагувати номер" : "Додати номер"}</button>
      </form>

      {/* Список номерів */}
      <ul>
        {rooms.map((room) => (
          <li key={room.id}>
            <p>
              {room.name} - {room.price} грн/день
            </p>
            <button onClick={() => handleEdit(room)}>Редагувати</button>
            <button onClick={() => handleDelete(room.id)}>Видалити</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageRoomsPage;
